@Component
public class ImageProcessing {
	@Autowired
	ImageProcessing imageProcessing;
	@Autowired
	MessageConfiguration messageConfiguration;
	
	Logger LOG = LoggerFactory.getLogger(ImageProcessing.class);
	public String getImageExtension(String base64String) throws GenericException{
		byte[] data = null;
		String extension;
		try {
			
//		String base64String = "data:image/png;base64,";
			String[] strings = base64String.split(",");
			if(strings.length == 1) {
				// invalid string
				throw new GenericException(messageConfiguration.getImageMessage().getInvalidBase64());
			}
			switch (strings[0]) {//check image's extension
			case "data:image/jpeg;base64":
				extension = "jpeg";
				break;
			case "data:image/png;base64":
				extension = "png";
				break;
			case "data:image/jpg;base64":
				extension = "png";
				break;
			default://should write cases for more images types
				throw new GenericException(messageConfiguration.getImageMessage().getInvalidBase64());
			}
			
			/*String path = "D:\\data\\test_image." + extension;
			file = new File(path);
			return file;
			// upload file
			try (OutputStream outputStream = new BufferedOutputStream(new FileOutputStream(file))) {
				outputStream.write(data);
			} catch (IOException e) {
				e.printStackTrace();
			}*/
		} catch (Exception e) {
			
			throw new GenericException(e.getMessage());
		}
		return extension;
	}
	public void createFileFromBase64(String base64String,String fullPath) throws GenericException{
		String[] strings = base64String.split(",");
		//convert base64 string to binary data
		byte[] data = DatatypeConverter.parseBase64Binary(strings[1]);
		//String path = "D:\\data\\test_image." + extension;
		File file = new File(fullPath);
		// upload file
		try (OutputStream outputStream = new BufferedOutputStream(new FileOutputStream(file))) {
			outputStream.write(data);
		} catch (IOException e) {
			LOG.error(e.getMessage());
			throw new GenericException(e.getMessage());
		}
	}
	
}
